mapper.select:
Select sno
FROM content WHERE
contentID = #{contentID}

mapper.update:
update content
SET score = #{score}
WHERE contentID = #{contentID}

UPDATE content
SET content = #{content}, fileName = #{fileName}, submitTime = #{submitTime}
WHERE contentID = #{contentID}

mapper.select:
SELECT homeworkID
FROM content
WHERE contentID = #{contentID}

SELECT *
FROM
    content
WHERE
    content.homeworkID = #{homeworkID}
        AND content.sno = #{sno};
